import React, {Component} from 'react';
import ItemList from "./ItemList";
import CostView from "./CostView";
import items from "../items";
import 'bootstrap/dist/css/bootstrap.css'

class App extends Component{

    constructor(props) {
        super(props);

        this.state = {
            finalCost : 0,
            items : items,
            isBuy : false
        };

        this.deleteItem = this.deleteItem.bind(this)

        this.calculateFinalCost();
    }

    deleteItem(index){
        console.log('---', 'deleted:', index)
        this.state.items.splice(index, 1);
        this.calculateFinalCost();
        this.setState(this.state);
    }

    calculateFinalCost(){
        let final_cost = 0;

        for (let i = 0; i < this.state.items.length; i++)
        {
            final_cost += this.state.items[i].cost
        }

        this.state.finalCost = final_cost;
        this.setState(this.state);
    }

    handleBuy(){
        this.state.isBuy = true;
        this.setState(this.state);
    }

    render() {

        let body = this.state.isBuy ? <div>Корзина пуста</div> :
            <div className="container">
                <h3 className="jumbotron">Корзина покупок</h3>
                <div>
                    <ItemList deleteItemFunc = {this.deleteItem} items={items}/>
                    <CostView finalCost = {this.state.finalCost} buyHandle = {this.handleBuy}/>
                </div>
            </div>
        return (
            {body}
        )
    }
}

export default App;
