import React from "react";

export default function CostView({items}) {
    var cost = 0;
    for (let item in items) {
        cost += item.cost;
    }
    return(
        <div className="card float-right">
            <h2 className="card-title">Итого {cost} Рублей</h2>
            <div className="card-body">
                <button className="btn btn-primary btn-lg badge-light">Купить</button>
            </div>
        </div>
    )
}
